    private void atualizarContatoNoCliqueDoMouse(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_atualizarContatoNoCliqueDoMouse
        if (jNome.getText().isEmpty()) {
            JOptionPane.showMessageDialog(null, "Nao foi possivel executar esta operacao, "
                    + "pois o campo nome esta nulo", "Erro", JOptionPane.ERROR_MESSAGE);
            return;
        }
        if (jEmail.getText().isEmpty()) {
            JOptionPane.showMessageDialog(null, "Nao foi possivel executar esta operacao, "
                    + "pois o campo email esta nulo", "Erro", JOptionPane.ERROR_MESSAGE);
            return;
        }
        if (jAniversario.getText().isEmpty()) {
            JOptionPane.showMessageDialog(null, "Nao foi possivel executar esta operacao, "
                    + "pois o campo aniversario esta nulo", "Erro", JOptionPane.ERROR_MESSAGE);
            return;
        }
        
        
        contato.setNome(jNome.getText());
        contato.setDtAniversario(jAniversario.getText());
        contato.setEmail(jEmail.getText());
        
        try {
            
            client.atualizarContato(contato);
            
            //jNome.setText(null);
            //jEmail.setText(null);
            //jAniversario.setText(null);
            
        } catch (RemoteException ex) {
            JOptionPane.showMessageDialog(null, "Houve um erro no momento de atualizar o Contato", 
                    "Erro", JOptionPane.ERROR_MESSAGE);
            
            ex.printStackTrace();
        }
    }//GEN-LAST:event_atualizarContatoNoCliqueDoMouse

    private void removerContatoNoCliqueDoMouse(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_removerContatoNoCliqueDoMouse
        if (contato == null) {
            JOptionPane.showMessageDialog(null, "Nao foi possivel executar esta operacao, "
                    + "pois o contato esta nulo", "Erro", JOptionPane.ERROR_MESSAGE);
            return;
        }
        
        try {
            
            client.removerContato(contato);
            telaPesquisar.avisarRemocao(contato);
            
            jNome.setText(null);
            jEmail.setText(null);
            jAniversario.setText(null);
        } catch (RemoteException ex) {
            JOptionPane.showMessageDialog(null, "Houve um erro no momento de remover o Contato", 
                    "Erro", JOptionPane.ERROR_MESSAGE);
            
            ex.printStackTrace();
        }
    }//GEN-LAST:event_removerContatoNoCliqueDoMouse

