    public void adicionarClient(AgendaClient client) {
        this.client = client;
    }
    
    public void adicionarContatoNaTela(Contato contatoParm) {
        this.contato = contatoParm;
        jNome.setText(contato.getNome());
        jAniversario.setText(contato.getDtAniversario());
        jEmail.setText(contato.getEmail());
    }
    
    public void adicionaTelaPesquisaContato(TelaPesquisar telaPesquisar) {
        this.telaPesquisar = telaPesquisar;
    }
