    private void salvarNoCliqueDoMouse(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_salvarNoCliqueDoMouse
        if (jNome.getText().isEmpty()) {
            JOptionPane.showMessageDialog(null, "Nao foi possivel executar esta operacao, "
                    + "pois o campo nome esta nulo", "Erro", JOptionPane.ERROR_MESSAGE);
        }
        if (jEmail.getText().isEmpty()) {
            JOptionPane.showMessageDialog(null, "Nao foi possivel executar esta operacao, "
                    + "pois o campo email esta nulo", "Erro", JOptionPane.ERROR_MESSAGE);
        }
        if (jAniversario.getText().isEmpty()) {
            JOptionPane.showMessageDialog(null, "Nao foi possivel executar esta operacao, "
                    + "pois o campo aniversario esta nulo", "Erro", JOptionPane.ERROR_MESSAGE);
        }
        
        contato = new Contato();
        contato.setNome(jNome.getText());
        contato.setDtAniversario(jAniversario.getText());
        contato.setEmail(jEmail.getText());
        
        try {
            client.inserirNovoContato(contato);
            
            JOptionPane.showMessageDialog(null, "Contato "+contato.getNome()+ 
                    " gravado com sucesso!", "Informa√ßao", JOptionPane.INFORMATION_MESSAGE);
            
            //limpar os campos da tela
            jNome.setText(null);
            jAniversario.setText(null);
            jEmail.setText(null);
            
        } catch (RemoteException ex) {
            JOptionPane.showMessageDialog(null, "Houve um erro no momento de salvar o Contato", 
                    "Erro", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_salvarNoCliqueDoMouse

